import React, { useState } from 'react';

const ProductManagement = ({ shop }) => {
  const [products, setProducts] = useState(() => {
    const initialData = {
      home: [
        { id: 1, name: '추천상품 1', description: '추천상품 설명', price: 10000, category: '추천상품', image: 'https://via.placeholder.com/150' },
        { id: 2, name: '이벤트상품 1', description: '이벤트상품 설명', price: 20000, category: '스페셜/이벤트', image: 'https://via.placeholder.com/150' },
        { id: 3, name: '인기상품 1', description: '인기상품 설명', price: 30000, category: '인기상품', image: 'https://via.placeholder.com/150' }
      ],
      newmajgo: [
        { id: 1, name: '스페셜상품 1', description: '스페셜상품 설명', price: 10000, category: '스페셜★', image: 'https://via.placeholder.com/150' },
        { id: 2, name: '알뜰팩 1', description: '알뜰팩 설명', price: 20000, category: '알뜰팩', image: 'https://via.placeholder.com/150' },
        { id: 3, name: '실속팩 1', description: '실속팩 설명', price: 30000, category: '실속팩', image: 'https://via.placeholder.com/150' },
        { id: 4, name: '아이템 1', description: '아이템 설명', price: 40000, category: '아이템', image: 'https://via.placeholder.com/150' },
        { id: 5, name: '핫딜 1', description: '핫딜 설명', price: 50000, category: '핫딜', image: 'https://via.placeholder.com/150' }
      ],
      poker: [
        { id: 1, name: '추천상품 1', description: '추천상품 설명', price: 10000, category: '추천', image: 'https://via.placeholder.com/150' },
        { id: 2, name: '이벤트상품 1', description: '이벤트상품 설명', price: 20000, category: '이벤트', image: 'https://via.placeholder.com/150' },
        { id: 3, name: '루비상품 1', description: '루비상품 설명', price: 30000, category: '루비', image: 'https://via.placeholder.com/150' },
        { id: 4, name: '아이템 1', description: '아이템 설명', price: 40000, category: '아이템', image: 'https://via.placeholder.com/150' }
      ]
    };
    return initialData[shop] || [];
  });

  const [editingProduct, setEditingProduct] = useState(null);

  const handleEditProduct = (product) => {
    setEditingProduct({ ...product });
  };

  const handleSaveEdit = () => {
    setProducts(products.map(product => 
      product.id === editingProduct.id ? editingProduct : product
    ));
    setEditingProduct(null);
  };

  const handleCancelEdit = () => {
    setEditingProduct(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditingProduct({
      ...editingProduct,
      [name]: value
    });
  };

  return (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-2xl font-bold mb-6">상품 관리</h2>

      <div className="grid grid-cols-1 gap-6">
        {products.map(product => (
          <div key={product.id} className="border rounded-lg p-4">
            {editingProduct?.id === product.id ? (
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">상품명</label>
                  <input
                    type="text"
                    name="name"
                    value={editingProduct.name}
                    onChange={handleInputChange}
                    className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">설명</label>
                  <textarea
                    name="description"
                    value={editingProduct.description}
                    onChange={handleInputChange}
                    className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                    rows="3"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">가격</label>
                  <input
                    type="number"
                    name="price"
                    value={editingProduct.price}
                    onChange={handleInputChange}
                    className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">카테고리</label>
                  <input
                    type="text"
                    name="category"
                    value={editingProduct.category}
                    onChange={handleInputChange}
                    className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">이미지 URL</label>
                  <input
                    type="text"
                    name="image"
                    value={editingProduct.image}
                    onChange={handleInputChange}
                    className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                  />
                </div>
                <div className="flex justify-end space-x-2">
                  <button
                    onClick={handleCancelEdit}
                    className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
                  >
                    취소
                  </button>
                  <button
                    onClick={handleSaveEdit}
                    className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
                  >
                    저장
                  </button>
                </div>
              </div>
            ) : (
              <div className="flex items-start space-x-4">
                <img src={product.image} alt={product.name} className="w-24 h-24 object-cover rounded" />
                <div className="flex-1">
                  <h3 className="text-lg font-medium">{product.name}</h3>
                  <p className="text-gray-600">{product.description}</p>
                  <div className="mt-2">
                    <span className="text-gray-500">카테고리: {product.category}</span>
                    <span className="ml-4 text-gray-500">가격: {product.price.toLocaleString()}원</span>
                  </div>
                </div>
                <button
                  onClick={() => handleEditProduct(product)}
                  className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
                >
                  수정
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductManagement;
