};

function ProductCard({ id, onDetailClick }) {
  const product = products[id];
  return (
    <div className="border rounded-lg shadow p-2 flex flex-col h-[320px] ">
      <div className="w-full mb-2 flex-shrink-0 relative">
        <img
          src={product.image}
          alt="상품 이미지"
          className="w-full h-[190px] object-contain rounded"
        />
        <button
          onClick={() => onDetailClick(id)}
          className="absolute top-2 right-2 p-2 bg-white/80 hover:bg-white rounded-full shadow transition-colors"
          aria-label="상세보기"
        >
          <HiSearch size={18} />
        </button>
      </div>
      <div className="flex flex-col flex-grow min-h-0">
        <h2 className="text-sm font-semibold mb-1 line-clamp-1">{product.name}</h2>
        <p className="text-xs text-gray-500 mb-2 line-clamp-2 flex-grow">{product.description}</p>
        <div className="mt-auto">
          <button className="w-full bg-red-500 hover:bg-red-600 text-white text-lg font-bold py-2 px-4 rounded-lg transition-colors">
            ₩{product.price.toLocaleString()}
          </button>
        </div>
      </div>
    </div>
  );
}

function ProductModal({ id, onClose }) {
  const product = products[id];
  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4"
      onClick={onClose}
    >
      <motion.div
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.9, opacity: 0 }}
        className="bg-white rounded-lg p-6 max-w-lg w-full"
        onClick={e => e.stopPropagation()}
      >
        <div className="flex justify-between items-start mb-4">
          <h2 className="text-xl font-bold">{product.name}</h2>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-full transition-colors"
          >
            <HiX size={24} />
          </button>
        </div>
        <img
          src={product.detailImage}
          alt={product.name}
          className="w-full h-64 object-contain mb-4 rounded"
        />
        <p className="text-gray-600 mb-4">{product.description}</p>
        <div className="space-y-2 mb-4">
          <h3 className="font-semibold">포함 아이템:</h3>
          <ul className="list-disc list-inside">
            {product.items.map((item, index) => (
              <li key={index} className="text-gray-600">{item}</li>
            ))}
          </ul>
        </div>
        <div className="flex justify-between items-center">
          <p className="text-2xl font-bold text-blue-600">{product.price.toLocaleString()}원</p>
          <button className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors">
            구매하기
          </button>
        </div>
      </motion.div>
    </motion.div>
  );
}

export default function ShopPage4() {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);

  const categories = ["추천상품", "스페셜/이벤트"];

  const shopLinks = [
    { name: "뉴맞고", path: "/shop2", icon: "🎮" },
    { name: "포커", path: "/shop3", icon: "🃏" },
    { name: "섯다", path: "/shop4", icon: "🎴" },
    { name: "쇼다운홀덤", path: "/shop5", icon: "♠️" }
  ];

  const getFilteredProducts = () => {
    return Object.entries(products).reduce((acc, [id, product]) => {
      if (!acc[product.category]) {
        acc[product.category] = [];
      }
      acc[product.category].push({ id, ...product });
      return acc;
    }, {});
  };

  const filteredProducts = getFilteredProducts();

  const sliderSettings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    centerMode: true,
    centerPadding: '0',
    focusOnSelect: true,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          centerMode: true,
        }
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 2,
          centerMode: true,
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 2,
          centerMode: true,
          slidesToScroll: 1,
        }
      }
    ]
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* GNB */}
      <nav className="bg-[#ffffff] shadow text-black flex items-center justify-between px-4 py-2">
        <div className="flex items-center gap-4">
          <a href="http://m.pmang.com/">
            <img
              src="data:image/png;base64,"
              alt="홈"
              className="h-5"
            />
          </a>
        </div>
        <div className="flex items-center gap-4">
          <button className="text-sm px-4 py-1 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors">
            로그인
          </button>
          <button
            className="p-2"
            onClick={() => setMenuOpen(!menuOpen)}
          >
            {menuOpen ? <HiX size={24} /> : <HiMenu size={24} />}
          </button>
        </div>
      </nav>
      {menuOpen && (
        <div className="absolute right-1 top-15 bg-white border rounded shadow p-4 z-10 w-60">
          <Link to="/" className="block text-blue-500 hover:text-blue-700 mb-4">
            피망 웹상점 홈
          </Link>
          <ul className="space-y-1">
            <li className="text-sm text-blue-500 hover:text-blue-700 cursor-pointer">
              <Link to="/shop2">뉴맞고 웹상점</Link>
            </li>
            <li className="text-sm text-blue-500 hover:text-blue-700 cursor-pointer">
              <Link to="/shop3">포커 웹상점</Link>
            </li>
          </ul>
        </div>
      )}

      {/* 피망 웹상점 헤더 */}
      <header className="flex justify-between items-center mt-6 mb-6">
        <div className="text-center w-full">
          <h1 className="text-2xl font-bold">피망 웹상점</h1>
          <p className="text-sm text-gray-500">원하는 게임 상품을 손쉽게 찾아보세요</p>
        </div>
      </header>

      
      {/* 메인 컨텐츠 */}
      <main className="max-w-7xl mx-auto px-2 py-2">
        {categories.map((category) => (
          <div key={category} className="mb-12">
            <h2 className="text-2xl font-bold mb-6 text-gray-900">{category}</h2>
            <div className="relative">
              <style>
                {`
                  .slick-list {
                    overflow: visible !important;
                  }
                  .slick-slide {
                    transition: all 0.3s cubic-bezier(.4,0,.2,1);
                    position: relative;
                    z-index: 1;
                    filter: blur(1px) grayscale(30%);
                    opacity: 0.6;
                    transform: scale(0.85) translateY(20px);
                  }
                  .slick-center {
                    z-index: 3 !important;
                    filter: none;
                    opacity: 1;
                    transform: scale(1.05) translateY(0);
                    box-shadow: 0 8px 32px 0 rgba(0,0,0,0.18);
                  }
                  .slick-active:not(.slick-center) {
                    z-index: 2 !important;
                    filter: blur(0.5px) grayscale(10%);
                    opacity: 0.85;
                    transform: scale(0.95) translateY(10px);
                  }
                  .slick-track {
                    display: flex;
                    align-items: center;
                  }
                  .slick-slide > div {
                    padding: 0 0px;
                  }
                  @media (max-width: 480px) {
                    .slick-slide {
                      transform: scale(0.9) translateY(10px);
                      opacity: 0.7;
                    }
                    .slick-center {
                      transform: scale(1) translateY(0);
                      opacity: 1;
                    }
                    .slick-list {
                      margin: 0 -20px;
                    }
                  }
                `}
              </style>
              <Slider {...sliderSettings}>
                {filteredProducts[category]?.map((product) => (
                  <div key={product.id}>
                    <ProductCard
                      id={product.id}
                      onDetailClick={setSelectedProduct}
                    />
                  </div>
                ))}
              </Slider>
            </div>
          </div>
        ))}

        {/* 상점 바로가기 영역 */}
        <div className="mt-12 mb-8">
          <h2 className="text-2xl font-bold mb-6 text-gray-900">상점 바로가기</h2>
          <div className="grid grid-cols-2 gap-4">
            {shopLinks.map((shop) => (
              <Link
                key={shop.name}
                to={shop.path}
                className="flex flex-col items-center justify-center p-6 bg-white rounded-lg shadow hover:shadow-md transition-shadow"
              >
                <span className="text-4xl mb-2">{shop.icon}</span>
                <span className="text-lg font-semibold text-gray-800">{shop.name}</span>
              </Link>
            ))}
          </div>
        </div>
      </main>

      {/* 상품 상세 모달 */}
      <AnimatePresence>
        {selectedProduct && (
          <ProductModal
            id={selectedProduct}
            onClose={() => setSelectedProduct(null)}
          />
        )}
      </AnimatePresence>
    </div>
  );
}
