    image: "https://dl.gostop.pmang.cloud/upload/bigdaebak_3b.png",
    detailImage: "https://dl.gostop.pmang.cloud/upload/Stochastic_Items.png"
  },
  4: {
    name: "초대박복권1장+화투패 +5억냥",
