import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const CategoryManagement = ({ shop }) => {
  const [categories, setCategories] = useState(() => {
    // 웹상점별 초기 카테고리 데이터
    const initialData = {
      home: [
        { id: 1, name: '추천', order: 1 },
        { id: 2, name: '이벤트', order: 2 },
        { id: 3, name: '루비', order: 3 }
      ],
      newmajgo: [
        { id: 1, name: '추천', order: 1 },
        { id: 2, name: '이벤트', order: 2 },
        { id: 3, name: '아이템', order: 3 }
      ],
      poker: [
        { id: 1, name: '추천', order: 1 },
        { id: 2, name: '이벤트', order: 2 },
        { id: 3, name: '포커 칩', order: 3 }
      ]
    };
    return initialData[shop] || [];
  });

  const [newCategory, setNewCategory] = useState('');

  const handleAddCategory = () => {
    if (newCategory.trim()) {
      const category = {
        id: Date.now(),
        name: newCategory.trim(),
        order: categories.length + 1
      };
      setCategories([...categories, category]);
      setNewCategory('');
    }
  };

  const handleDeleteCategory = (id) => {
    setCategories(categories.filter(category => category.id !== id));
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const items = Array.from(categories);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    // 순서 업데이트
    const updatedItems = items.map((item, index) => ({
      ...item,
      order: index + 1
    }));

    setCategories(updatedItems);
  };

  const handleOrderChange = (id, newOrder) => {
    const numOrder = parseInt(newOrder);
    if (isNaN(numOrder) || numOrder < 1 || numOrder > categories.length) return;

    const updatedCategories = [...categories];
    const categoryIndex = updatedCategories.findIndex(cat => cat.id === id);
    
    if (categoryIndex !== -1) {
      // 현재 순서와 새로운 순서 사이의 카테고리들을 조정
      const currentOrder = updatedCategories[categoryIndex].order;
      if (numOrder > currentOrder) {
        // 아래로 이동
        for (let i = 0; i < updatedCategories.length; i++) {
          if (updatedCategories[i].order > currentOrder && updatedCategories[i].order <= numOrder) {
            updatedCategories[i].order--;
          }
        }
      } else if (numOrder < currentOrder) {
        // 위로 이동
        for (let i = 0; i < updatedCategories.length; i++) {
          if (updatedCategories[i].order >= numOrder && updatedCategories[i].order < currentOrder) {
            updatedCategories[i].order++;
          }
        }
      }
      
      updatedCategories[categoryIndex].order = numOrder;
      
      // 순서대로 정렬
      updatedCategories.sort((a, b) => a.order - b.order);
      
      setCategories(updatedCategories);
    }
  };

  return (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-2xl font-bold mb-6">카테고리 관리</h2>
      
      <div className="mb-6">
        <div className="flex gap-4">
          <input
            type="text"
            value={newCategory}
            onChange={(e) => setNewCategory(e.target.value)}
            placeholder="새 카테고리 이름"
            className="flex-1 border rounded px-3 py-2"
          />
          <button
            onClick={handleAddCategory}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            추가
          </button>
        </div>
      </div>

      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="categories">
          {(provided) => (
            <div
              {...provided.droppableProps}
              ref={provided.innerRef}
              className="space-y-2"
            >
              {categories.map((category, index) => (
                <Draggable
                  key={category.id}
                  draggableId={category.id.toString()}
                  index={index}
                >
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      className="flex items-center gap-4 p-4 border rounded bg-white"
                    >
                      <div className="flex-1 flex items-center gap-4">
                        <span className="text-gray-500">순서: {category.order}</span>
                        <span className="font-medium">{category.name}</span>
                      </div>
                      <div className="flex items-center gap-2">
                        <input
                          type="number"
                          min="1"
                          max={categories.length}
                          value={category.order}
                          onChange={(e) => handleOrderChange(category.id, e.target.value)}
                          className="w-16 border rounded px-2 py-1 text-center"
                        />
                        <button
                          onClick={() => handleDeleteCategory(category.id)}
                          className="text-red-500 hover:text-red-600"
                        >
                          삭제
                        </button>
                      </div>
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default CategoryManagement;
