import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // useNavigate 추가

const AddShop = () => {
  const [shopName, setShopName] = useState('');
  const [shopDescription, setShopDescription] = useState('');
  const [shopImage, setShopImage] = useState('');
  const [shopCategory, setShopCategory] = useState('');
  const [shopList, setShopList] = useState([]); // 상점 리스트 상태 추가
  const navigate = useNavigate(); // useNavigate 사용

  const handleAddShop = (e) => {
    e.preventDefault();
    // 상점 추가 로직 구현
    const newShop = {
      name: shopName,
      description: shopDescription,
      image: shopImage,
      category: shopCategory,
    };
    console.log('새 상점 추가:', newShop);

    // 상점 리스트에 추가
    setShopList([
      ...shopList,
      { name: '피망 웹상점(홈)', description: '피망 웹상점 설명', category: '카테고리' },
      { name: '뉴맞고 웹상점', description: '뉴맞고 웹상점 설명', category: '카테고리' },
      { name: '포커 웹상점', description: '포커 웹상점 설명', category: '카테고리' },
      newShop
    ]);

    // 추가 후 초기화
    setShopName('');
    setShopDescription('');
    setShopImage('');
    setShopCategory('');
  };

  const handleDeleteShop = (index) => {
    // 상점 삭제 로직 구현
    const updatedShopList = shopList.filter((_, i) => i !== index);
    setShopList(updatedShopList); // 삭제 후 리스트 업데이트
    console.log('상점 삭제:', shopList[index].name);
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="flex">
        <div className="w-1/2 p-6 bg-white rounded-lg shadow-md">
          <h2 className="text-2xl font-bold mb-4">상점 추가</h2>
          <form onSubmit={handleAddShop}>
            <div>
              <label className="block text-sm font-medium text-gray-700">상점 이름</label>
              <input
                type="text"
                value={shopName}
                onChange={(e) => setShopName(e.target.value)}
                className="border rounded px-3 py-2 w-full"
                required
              />
            </div>
            <div className="mt-4">
              <label className="block text-sm font-medium text-gray-700">상점 설명</label>
              <textarea
                value={shopDescription}
                onChange={(e) => setShopDescription(e.target.value)}
                className="border rounded px-3 py-2 w-full"
                rows="4"
                required
              />
            </div>
            <div className="mt-4">
              <label className="block text-sm font-medium text-gray-700">상점 이미지</label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  const file = e.target.files[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => {
                      setShopImage(reader.result); // 상점 이미지 상태 업데이트
                    };
                    reader.readAsDataURL(file);
                  }
                }}
                className="block w-full text-sm text-gray-500
                  file:mr-4 file:py-2 file:px-4
                  file:rounded-md file:border-0
                  file:text-sm file:font-semibold
                  file:bg-blue-50 file:text-blue-700
                  hover:file:bg-blue-100"
              />
              {shopImage && (
                <div className="w-24 h-24 border rounded-md overflow-hidden mt-2">
                  <img
                    src={shopImage}
                    alt="상점 이미지 미리보기"
                    className="w-full h-full object-cover"
                  />
                </div>
              )}
            </div>
            <div className="mt-4">
              <label className="block text-sm font-medium text-gray-700">상점 카테고리</label>
              <input
                type="text"
                value={shopCategory}
                onChange={(e) => setShopCategory(e.target.value)}
                className="border rounded px-3 py-2 w-full"
                required
              />
            </div>
            <button
              type="submit"
              className="mt-4 bg-blue-500 text-white px-4 py-2 rounded"
            >
              추가
            </button>
          </form>
        </div>
        <div className="w-1/2 p-6 bg-white rounded-lg shadow-md ml-4">
          <h3 className="text-lg font-semibold mt-6">상점 리스트</h3>
          <ul className="mt-2">
            {shopList.map((shop, index) => (
              <li key={index} className="flex justify-between items-center border-b py-2">
                <div>
                  <strong>{shop.name}</strong> - {shop.category}
                </div>
                <button
                  onClick={() => handleDeleteShop(index)}
                  className="text-red-500 hover:underline"
                >
                  삭제
                </button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default AddShop;
