import React, { useState } from 'react';

const ProductManagement = ({ shop }) => {
  const [products, setProducts] = useState(() => {
    // 웹상점별 초기 상품 데이터
    const initialData = {
      home: [
        {
          id: 1,
          name: "반짝 스페셜 오퍼",
          description: "AVA에서 사용 가능한 한정판 무기 아이템이 포함된 패키지입니다.",
          price: 9900,
          category: "추천상품",
          image: "https://dl.gostop.pmang.cloud/upload/Dia_653_1707.png"
        },
        {
          id: 2,
          name: "월구매제한) 황금편자(10일)",
          description: "황금편자 아이템 10일 이용권",
          price: 9900,
          category: "추천상품",
          image: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_11000.png"
        }
      ],
      newmajgo: [
        {
          id: 1,
          name: "행운플러스",
          description: "행운플러스 상품",
          price: 5900,
          category: "알뜰팩",
          image: "https://dl.gostop.pmang.cloud/upload/luckyplus_shop.png"
        },
        {
          id: 2,
          name: "초대박복권2장+화투패 + 14억냥",
          description: "초대박복권 2장과 화투패, 14억냥 제공",
          price: 44000,
          category: "스페셜(★)",
          image: "https://dl.gostop.pmang.cloud/upload/bigdaebak_20b.png"
        }
      ],
      poker: [
        {
          id: 1,
          name: "클래식 잭(15일)",
          description: "클래식 잭 아이템 15일 이용권",
          price: 11900,
          category: "추천",
          image: "https://dl.poker.pmang.cloud/upload/poker_special1.png"
        },
        {
          id: 2,
          name: "클래식 잭(30일)",
          description: "클래식 잭 아이템 30일 이용권",
          price: 19900,
          category: "추천",
          image: "https://dl.poker.pmang.cloud/upload/poker_special1.png"
        }
      ]
    };
    return initialData[shop] || [];
  });

  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    image: ''
  });

  const handleAddProduct = () => {
    if (newProduct.name && newProduct.price) {
      const product = {
        id: Date.now(),
        ...newProduct,
        price: parseInt(newProduct.price)
      };
      setProducts([...products, product]);
      setNewProduct({
        name: '',
        description: '',
        price: '',
        category: '',
        image: ''
      });
    }
  };

  const handleDeleteProduct = (id) => {
    setProducts(products.filter(product => product.id !== id));
  };

  const handleUpdateProduct = (id, field, value) => {
    setProducts(products.map(product => 
      product.id === id ? { ...product, [field]: value } : product
    ));
  };

  return (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-2xl font-bold mb-6">상품 관리</h2>
      
      <div className="mb-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input
            type="text"
            value={newProduct.name}
            onChange={(e) => setNewProduct({...newProduct, name: e.target.value})}
            placeholder="상품명"
            className="border rounded px-3 py-2"
          />
          <input
            type="text"
            value={newProduct.description}
            onChange={(e) => setNewProduct({...newProduct, description: e.target.value})}
            placeholder="상품 설명"
            className="border rounded px-3 py-2"
          />
          <input
            type="number"
            value={newProduct.price}
            onChange={(e) => setNewProduct({...newProduct, price: e.target.value})}
            placeholder="가격"
            className="border rounded px-3 py-2"
          />
          <input
            type="text"
            value={newProduct.category}
            onChange={(e) => setNewProduct({...newProduct, category: e.target.value})}
            placeholder="카테고리"
            className="border rounded px-3 py-2"
          />
          <input
            type="text"
            value={newProduct.image}
            onChange={(e) => setNewProduct({...newProduct, image: e.target.value})}
            placeholder="이미지 URL"
            className="border rounded px-3 py-2"
          />
        </div>
        <button
          onClick={handleAddProduct}
          className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          상품 추가
        </button>
      </div>

      <div className="space-y-4">
        {products.map(product => (
          <div key={product.id} className="border rounded p-4">
            <div className="flex justify-between items-start">
              <div className="flex-1">
                <div className="flex items-center gap-4 mb-2">
                  <h3 className="font-bold">{product.name}</h3>
                  <span className="text-sm text-gray-500">₩{product.price.toLocaleString()}</span>
                </div>
                <p className="text-sm text-gray-500 mb-2">{product.description}</p>
                <p className="text-sm text-blue-500">카테고리: {product.category}</p>
                {product.image && (
                  <img 
                    src={product.image} 
                    alt={product.name}
                    className="mt-2 max-w-xs rounded"
                  />
                )}
              </div>
              <button
                onClick={() => handleDeleteProduct(product.id)}
                className="text-red-500 hover:text-red-600"
              >
                삭제
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductManagement;
