import React, { useState } from "react";
import { HiX, HiSearch } from "react-icons/hi";
import { motion, AnimatePresence } from "framer-motion";

const products = {
  1: {
    name: "프리미엄 스타터 팩",
    description: "게임을 시작하기 위한 최고의 패키지",
    price: 15000,
    items: ["프리미엄 아이템", "스타터 코인", "30일 이용권"],
    category: "추천상품",
    image: "https://dl.gostop.pmang.cloud/upload/Dia_653_1707.png",
    detailImage: "https://dl.gostop.pmang.cloud/upload/Dia_653_1707.png"
  },
  2: {
    name: "골드 멤버십(30일)",
    description: "골드 멤버십 30일 이용권",
    price: 29900,
    items: ["골드 멤버십", "30일 이용권"],
    category: "추천상품",
    image: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_11000.png",
    detailImage: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_11000.png"
  },
  3: {
    name: "플래티넘 패키지",
    description: "플래티넘 등급 전용 패키지",
    price: 49900,
    items: ["플래티넘 아이템", "특별 보너스"],
    category: "추천상품",
    image: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_22000.png",
    detailImage: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_22000.png"
  },
  4: {
    name: "한정판 스페셜 팩",
    description: "한정판 스페셜 아이템 모음",
    price: 39900,
    items: ["한정판 아이템", "특별 보너스", "15일 이용권"],
    category: "스페셜/이벤트",
    image: "https://dl.gostop.pmang.cloud/upload/Dia_653_1707.png",
    detailImage: "https://dl.gostop.pmang.cloud/upload/Dia_653_1707.png"
  },
  5: {
    name: "이벤트 특별 패키지",
    description: "이벤트 기간 한정 특별 패키지",
    price: 19900,
    items: ["이벤트 아이템", "보너스 코인"],
    category: "스페셜/이벤트",
    image: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_11000.png",
    detailImage: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_11000.png"
  },
  6: {
    name: "시즌 패스",
    description: "시즌 한정 특별 패스",
    price: 29900,
    items: ["시즌 아이템", "특별 보너스"],
    category: "스페셜/이벤트",
    image: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_22000.png",
    detailImage: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_22000.png"
  },
  7: {
    name: "베이직 패키지",
    description: "기본적인 게임 아이템 모음",
    price: 9900,
    items: ["기본 아이템", "보너스 코인"],
    category: "인기상품",
    image: "https://dl.gostop.pmang.cloud/upload/Dia_653_1707.png",
    detailImage: "https://dl.gostop.pmang.cloud/upload/Dia_653_1707.png"
  },
  8: {
    name: "스탠다드 패키지",
    description: "표준 게임 아이템 세트",
    price: 19900,
    items: ["표준 아이템", "보너스 코인"],
    category: "인기상품",
    image: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_11000.png",
    detailImage: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_11000.png"
  },
  9: {
    name: "프리미엄 코인 팩",
    description: "프리미엄 코인 대량 패키지",
    price: 29900,
    items: ["프리미엄 코인", "보너스 아이템"],
    category: "인기상품",
    image: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_22000.png",
    detailImage: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_22000.png"
  }
};

function ProductCard({ id, onDetailClick }) {
  const product = products[id];
  return (
    <div className="border rounded-lg shadow p-2 flex flex-col h-[320px]">
      <div className="w-full mb-2 flex-shrink-0 relative">
        <img
          src={product.image}
          alt="상품 이미지"
          className="w-full h-[190px] object-contain rounded"
        />
        <button
          onClick={() => onDetailClick(id)}
          className="absolute top-2 right-2 p-2 bg-white/80 hover:bg-white rounded-full shadow transition-colors"
          aria-label="상세보기"
        >
          <HiSearch size={18} />
        </button>
      </div>
      <div className="flex flex-col flex-grow min-h-0">
        <h2 className="text-sm font-semibold mb-1 line-clamp-1">{product.name}</h2>
        <p className="text-xs text-gray-500 mb-2 line-clamp-2 flex-grow">{product.description}</p>
        <div className="mt-auto">
          <p className="text-lg font-bold text-blue-600">{product.price.toLocaleString()}원</p>
        </div>
      </div>
    </div>
  );
}

function ProductModal({ id, onClose }) {
  const product = products[id];
  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4"
      onClick={onClose}
    >
      <motion.div
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.9, opacity: 0 }}
        className="bg-white rounded-lg p-6 max-w-lg w-full"
        onClick={e => e.stopPropagation()}
      >
        <div className="flex justify-between items-start mb-4">
          <h2 className="text-xl font-bold">{product.name}</h2>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-full transition-colors"
          >
            <HiX size={24} />
          </button>
        </div>
        <img
          src={product.detailImage}
          alt={product.name}
          className="w-full h-64 object-contain mb-4 rounded"
        />
        <p className="text-gray-600 mb-4">{product.description}</p>
        <div className="space-y-2 mb-4">
          <h3 className="font-semibold">포함 아이템:</h3>
          <ul className="list-disc list-inside">
            {product.items.map((item, index) => (
              <li key={index} className="text-gray-600">{item}</li>
            ))}
          </ul>
        </div>
        <div className="flex justify-between items-center">
          <p className="text-2xl font-bold text-blue-600">{product.price.toLocaleString()}원</p>
          <button className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors">
            구매하기
          </button>
        </div>
      </motion.div>
    </motion.div>
  );
}

const ShopPage4 = () => {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState("추천상품");

  const categories = ["추천상품", "스페셜/이벤트", "인기상품"];

  const getFilteredProducts = () => {
    return Object.entries(products).filter(([_, product]) => 
      product.category === selectedCategory
    );
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-2xl font-bold">프리미엄 상점</h1>
          <div className="flex space-x-4">
            {categories.map(category => (
              <button
                key={category}
                onClick={() => setSelectedCategory(category)}
                className={`px-4 py-2 rounded-lg transition-colors ${
                  selectedCategory === category
                    ? "bg-blue-600 text-white"
                    : "bg-white text-gray-600 hover:bg-gray-100"
                }`}
              >
                {category}
              </button>
            ))}
          </div>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {getFilteredProducts().map(([id, _]) => (
            <ProductCard
              key={id}
              id={id}
              onDetailClick={setSelectedProduct}
            />
          ))}
        </div>
      </div>

      <AnimatePresence>
        {selectedProduct && (
          <ProductModal
            id={selectedProduct}
            onClose={() => setSelectedProduct(null)}
          />
        )}
      </AnimatePresence>
    </div>
  );
};

export default ShopPage4;
