import React, { useState } from 'react';

const BannerManagement = () => {
  const [banners, setBanners] = useState([]);
  const [newBanner, setNewBanner] = useState({
    title: '',
    image: '',
    link: '',
    priority: ''
  });

  const handleAddBanner = () => {
    if (newBanner.image) {
      const banner = {
        id: Date.now(),
        ...newBanner
      };
      setBanners([...banners, banner]);
      setNewBanner({
        title: '',
        image: '',
        link: '',
        priority: ''
      });
    }
  };

  const handleDeleteBanner = (id) => {
    setBanners(banners.filter(banner => banner.id !== id));
  };

  return (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-2xl font-bold mb-6">배너 관리</h2>
      
      <div className="mb-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input
            type="text"
            value={newBanner.title}
            onChange={(e) => setNewBanner({...newBanner, title: e.target.value})}
            placeholder="배너 제목"
            className="border rounded px-3 py-2"
          />
          <input
            type="text"
            value={newBanner.image}
            onChange={(e) => setNewBanner({...newBanner, image: e.target.value})}
            placeholder="배너 이미지 URL"
            className="border rounded px-3 py-2"
          />
          <input
            type="text"
            value={newBanner.link}
            onChange={(e) => setNewBanner({...newBanner, link: e.target.value})}
            placeholder="링크 URL"
            className="border rounded px-3 py-2"
          />
          <input
            type="number"
            value={newBanner.priority}
            onChange={(e) => setNewBanner({...newBanner, priority: e.target.value})}
            placeholder="우선순위"
            className="border rounded px-3 py-2"
          />
        </div>
        <button
          onClick={handleAddBanner}
          className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          배너 추가
        </button>
      </div>

      <div className="space-y-4">
        {banners.map(banner => (
          <div key={banner.id} className="border rounded p-4">
            <div className="flex justify-between items-start">
              <div>
                <h3 className="font-bold">{banner.title}</h3>
                <p className="text-sm text-gray-500">우선순위: {banner.priority}</p>
                <p className="text-sm text-blue-500">{banner.link}</p>
                {banner.image && (
                  <img 
                    src={banner.image} 
                    alt={banner.title}
                    className="mt-2 max-w-xs rounded"
                  />
                )}
              </div>
              <button
                onClick={() => handleDeleteBanner(banner.id)}
                className="text-red-500 hover:text-red-600"
              >
                삭제
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BannerManagement;
