import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const CategoryManagement = () => {
  const [categories, setCategories] = useState([
    { id: 1, name: '전체', priority: 1 },
    { id: 2, name: '추천상품', priority: 2 },
    { id: 3, name: '스페셜/이벤트', priority: 3 },
    { id: 4, name: '인기상품', priority: 4 }
  ]);
  const [newCategory, setNewCategory] = useState('');
  const [editingCategory, setEditingCategory] = useState(null);

  const handleAddCategory = () => {
    if (newCategory.trim()) {
      const newId = Math.max(...categories.map(c => c.id)) + 1;
      setCategories([...categories, { id: newId, name: newCategory, priority: categories.length + 1 }]);
      setNewCategory('');
    }
  };

  const handleDeleteCategory = (id) => {
    setCategories(categories.filter(category => category.id !== id));
  };

  const handleEditCategory = (category) => {
    setEditingCategory(category);
  };

  const handleSaveEdit = () => {
    setCategories(categories.map(category => 
      category.id === editingCategory.id ? editingCategory : category
    ));
    setEditingCategory(null);
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const items = Array.from(categories);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    // 우선순위 업데이트
    const updatedItems = items.map((item, index) => ({
      ...item,
      priority: index + 1
    }));

    setCategories(updatedItems);
  };

  return (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-2xl font-bold mb-6">카테고리 관리</h2>

      <div className="mb-6">
        <div className="flex gap-4">
          <input
            type="text"
            value={newCategory}
            onChange={(e) => setNewCategory(e.target.value)}
            placeholder="새 카테고리 이름"
            className="flex-1 border rounded px-3 py-2"
          />
          <button
            onClick={handleAddCategory}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            추가
          </button>
        </div>
      </div>

      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="categories">
          {(provided) => (
            <div
              {...provided.droppableProps}
              ref={provided.innerRef}
              className="space-y-2"
            >
              {categories.map((category, index) => (
                <Draggable
                  key={category.id}
                  draggableId={category.id.toString()}
                  index={index}
                >
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      className="flex items-center justify-between bg-gray-50 p-4 rounded"
                    >
                      <div className="flex items-center gap-4">
                        <span className="text-gray-500">#{category.priority}</span>
                        {editingCategory?.id === category.id ? (
                          <input
                            type="text"
                            value={editingCategory.name}
                            onChange={(e) => setEditingCategory({
                              ...editingCategory,
                              name: e.target.value
                            })}
                            className="border rounded px-2 py-1"
                          />
                        ) : (
                          <span>{category.name}</span>
                        )}
                      </div>
                      <div className="flex gap-2">
                        {editingCategory?.id === category.id ? (
                          <button
                            onClick={handleSaveEdit}
                            className="text-green-500 hover:text-green-600"
                          >
                            저장
                          </button>
                        ) : (
                          <button
                            onClick={() => handleEditCategory(category)}
                            className="text-blue-500 hover:text-blue-600"
                          >
                            수정
                          </button>
                        )}
                        <button
                          onClick={() => handleDeleteCategory(category.id)}
                          className="text-red-500 hover:text-red-600"
                        >
                          삭제
                        </button>
                      </div>
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default CategoryManagement;
