import React, { useState } from 'react';
import { HiEye } from 'react-icons/hi';

const ProductManagement = ({ shop }) => {
  const [products, setProducts] = useState(() => {
    const initialData = {
      home: [
        {
          id: 1,
          productId: "P001",
          name: "반짝 스페셜 오퍼",
          adminTag: "추천",
          displayName: "반짝 스페셜 오퍼",
          price: 9900,
          startDate: "2024-03-01",
          endDate: "2024-03-31",
          description: "AVA에서 사용 가능한 한정판 무기 아이템이 포함된 패키지입니다.",
          description2: "한정판 무기 아이템으로 게임을 더욱 즐겁게 즐기세요.",
          image: "https://dl.gostop.pmang.cloud/upload/Dia_653_1707.png",
          detailImage: "https://dl.gostop.pmang.cloud/upload/Dia_653_1707_detail.png",
          isVisible: true,
          category: "추천상품"
        },
        {
          id: 2,
          productId: "P002",
          name: "월구매제한) 황금편자(10일)",
          adminTag: "이벤트",
          displayName: "황금편자 10일 이용권",
          price: 9900,
          startDate: "2024-03-01",
          endDate: "2024-03-31",
          description: "황금편자 아이템 10일 이용권",
          description2: "황금편자로 더욱 특별한 게임 경험을 즐기세요.",
          image: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_11000.png",
          detailImage: "https://dl.poker.pmang.cloud/upload/mp_mob_ruby_11000_detail.png",
          isVisible: true,
          category: "추천상품"
        }
      ],
      newmajgo: [
        {
          id: 1,
          productId: "NM001",
          name: "행운플러스",
          adminTag: "스페셜",
          displayName: "행운플러스 패키지",
          price: 5900,
          startDate: "2024-03-01",
          endDate: "2024-03-31",
          description: "행운플러스 상품",
          description2: "행운을 더해주는 특별한 패키지",
          image: "https://dl.gostop.pmang.cloud/upload/luckyplus_shop.png",
          detailImage: "https://dl.gostop.pmang.cloud/upload/luckyplus_shop_detail.png",
          isVisible: true,
          category: "알뜰팩"
        },
        {
          id: 2,
          productId: "NM002",
          name: "초대박복권2장+화투패 + 14억냥",
          adminTag: "핫딜",
          displayName: "초대박복권 패키지",
          price: 44000,
          startDate: "2024-03-01",
          endDate: "2024-03-31",
          description: "초대박복권 2장과 화투패, 14억냥 제공",
          description2: "대박의 기회를 놓치지 마세요!",
          image: "https://dl.gostop.pmang.cloud/upload/bigdaebak_20b.png",
          detailImage: "https://dl.gostop.pmang.cloud/upload/bigdaebak_20b_detail.png",
          isVisible: true,
          category: "스페셜(★)"
        }
      ],
      poker: [
        {
          id: 1,
          productId: "PK001",
          name: "클래식 잭(15일)",
          adminTag: "추천",
          displayName: "클래식 잭 15일 이용권",
          price: 11900,
          startDate: "2024-03-01",
          endDate: "2024-03-31",
          description: "클래식 잭 아이템 15일 이용권",
          description2: "클래식 잭으로 더욱 특별한 포커 경험을 즐기세요.",
          image: "https://dl.poker.pmang.cloud/upload/poker_special1.png",
          detailImage: "https://dl.poker.pmang.cloud/upload/poker_special1_detail.png",
          isVisible: true,
          category: "추천"
        },
        {
          id: 2,
          productId: "PK002",
          name: "클래식 잭(30일)",
          adminTag: "추천",
          displayName: "클래식 잭 30일 이용권",
          price: 19900,
          startDate: "2024-03-01",
          endDate: "2024-03-31",
          description: "클래식 잭 아이템 30일 이용권",
          description2: "클래식 잭으로 더욱 특별한 포커 경험을 즐기세요.",
          image: "https://dl.poker.pmang.cloud/upload/poker_special1.png",
          detailImage: "https://dl.poker.pmang.cloud/upload/poker_special1_detail.png",
          isVisible: true,
          category: "추천"
        }
      ]
    };
    return initialData[shop] || [];
  });

  const [editingProduct, setEditingProduct] = useState(null);
  const [showPreview, setShowPreview] = useState(false);
  const [previewProduct, setPreviewProduct] = useState(null);

  const handleEditProduct = (product) => {
    setEditingProduct({ ...product });
    setShowPreview(false);
  };

  const handleSaveEdit = () => {
    setProducts(products.map(product => 
      product.id === editingProduct.id ? editingProduct : product
    ));
    setEditingProduct(null);
    setShowPreview(false);
  };

  const handleCancelEdit = () => {
    setEditingProduct(null);
    setShowPreview(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditingProduct({
      ...editingProduct,
      [name]: value
    });
  };

  const toggleVisibility = (productId) => {
    setProducts(products.map(product => 
      product.id === productId 
        ? { ...product, isVisible: !product.isVisible }
        : product
    ));
  };

  const handlePreview = (product) => {
    setPreviewProduct(product);
    setShowPreview(true);
  };

  const closePreview = () => {
    setShowPreview(false);
    setPreviewProduct(null);
  };

  return (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-2xl font-bold mb-6">상품 관리</h2>

      {/* 상세보기 미리보기 모달 */}
      {showPreview && previewProduct && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-2xl w-full max-h-[90vh] overflow-y-auto">
            <div className="flex justify-between items-start mb-4">
              <h3 className="text-xl font-bold">{previewProduct.name}</h3>
              <button
                onClick={closePreview}
                className="text-gray-500 hover:text-gray-700"
              >
                ✕
              </button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <img
                  src={previewProduct.detailImage}
                  alt={previewProduct.name}
                  className="w-full h-auto rounded-lg"
                />
              </div>
              <div>
                <div className="mb-4">
                  <h4 className="font-medium text-gray-700 mb-2">상품 설명</h4>
                  <p className="text-gray-600">{previewProduct.description}</p>
                </div>
                <div className="mb-4">
                  <h4 className="font-medium text-gray-700 mb-2">추가 설명</h4>
                  <p className="text-gray-600">{previewProduct.description2}</p>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-2xl font-bold text-gray-900">
                    {previewProduct.price.toLocaleString()}원
                  </span>
                  <button className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600">
                    구매하기
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="grid grid-cols-1 gap-6">
        {products.map(product => (
          <div key={product.id} className="border rounded-lg p-4">
            {editingProduct?.id === product.id ? (
              <div className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">상품ID</label>
                    <input
                      type="text"
                      name="productId"
                      value={editingProduct.productId}
                      onChange={handleInputChange}
                      className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">상품명</label>
                    <input
                      type="text"
                      name="name"
                      value={editingProduct.name}
                      onChange={handleInputChange}
                      className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Admin TAG</label>
                    <input
                      type="text"
                      name="adminTag"
                      value={editingProduct.adminTag}
                      onChange={handleInputChange}
                      className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">노출이름</label>
                    <input
                      type="text"
                      name="displayName"
                      value={editingProduct.displayName}
                      onChange={handleInputChange}
                      className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">가격</label>
                    <input
                      type="number"
                      name="price"
                      value={editingProduct.price}
                      onChange={handleInputChange}
                      className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">카테고리</label>
                    <input
                      type="text"
                      name="category"
                      value={editingProduct.category}
                      onChange={handleInputChange}
                      className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">노출시작일</label>
                    <input
                      type="date"
                      name="startDate"
                      value={editingProduct.startDate}
                      onChange={handleInputChange}
                      className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">노출종료일</label>
                    <input
                      type="date"
                      name="endDate"
                      value={editingProduct.endDate}
                      onChange={handleInputChange}
                      className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                    />
                  </div>
                </div>
                <div>
                  <div className="flex justify-between items-center">
                    <label className="block text-sm font-medium text-gray-700">상품 상세설명</label>
                    <button
                      onClick={togglePreview}
                      className="text-sm text-blue-500 hover:text-blue-600"
                    >
                      {showPreview ? '편집하기' : '미리보기'}
                    </button>
                  </div>
                  {showPreview ? (
                    <div className="mt-2 p-4 border rounded bg-gray-50">
                      <h4 className="font-medium mb-2">상품 상세설명 미리보기</h4>
                      <p className="text-gray-700">{editingProduct.description}</p>
                      <h4 className="font-medium mt-4 mb-2">상품 상세설명2 미리보기</h4>
                      <p className="text-gray-700">{editingProduct.description2}</p>
                    </div>
                  ) : (
                    <>
                      <textarea
                        name="description"
                        value={editingProduct.description}
                        onChange={handleInputChange}
                        className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                        rows="3"
                      />
                      <div className="mt-4">
                        <label className="block text-sm font-medium text-gray-700">상품 상세설명2</label>
                        <textarea
                          name="description2"
                          value={editingProduct.description2}
                          onChange={handleInputChange}
                          className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                          rows="3"
                        />
                      </div>
                    </>
                  )}
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">상품이미지 URL</label>
                  <input
                    type="text"
                    name="image"
                    value={editingProduct.image}
                    onChange={handleInputChange}
                    className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">상세보기 이미지 URL</label>
                  <input
                    type="text"
                    name="detailImage"
                    value={editingProduct.detailImage}
                    onChange={handleInputChange}
                    className="mt-1 block w-full border rounded-md shadow-sm py-2 px-3"
                  />
                </div>
                <div className="flex justify-end space-x-2">
                  <button
                    onClick={handleCancelEdit}
                    className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
                  >
                    취소
                  </button>
                  <button
                    onClick={handleSaveEdit}
                    className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
                  >
                    저장
                  </button>
                </div>
              </div>
            ) : (
              <div className="flex items-start space-x-4">
                <img src={product.image} alt={product.name} className="w-24 h-24 object-cover rounded" />
                <div className="flex-1">
                  <div className="flex items-center space-x-2">
                    <h3 className="text-lg font-medium">{product.name}</h3>
                    <span className="text-sm text-gray-500">({product.productId})</span>
                  </div>
                  <div className="mt-1">
                    <span className="text-sm text-blue-500">Admin TAG: {product.adminTag}</span>
                    <span className="ml-4 text-sm text-gray-500">노출이름: {product.displayName}</span>
                  </div>
                  <p className="text-gray-600 mt-2">{product.description}</p>
                  <div className="mt-2">
                    <span className="text-gray-500">카테고리: {product.category}</span>
                    <span className="ml-4 text-gray-500">가격: {product.price.toLocaleString()}원</span>
                  </div>
                  <div className="mt-1 text-sm text-gray-500">
                    노출기간: {product.startDate} ~ {product.endDate}
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  <button
                    onClick={() => toggleVisibility(product.id)}
                    className={`px-3 py-1 rounded-md text-sm ${
                      product.isVisible 
                        ? 'bg-green-500 text-white hover:bg-green-600' 
                        : 'bg-red-500 text-white hover:bg-red-600'
                    }`}
                  >
                    {product.isVisible ? '노출중' : '숨김'}
                  </button>
                  <button
                    onClick={() => handleEditProduct(product)}
                    className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
                  >
                    수정
                  </button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductManagement;
