import React, { useState } from 'react';
import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import CategoryManagement from './CategoryManagement';
import ProductManagement from './ProductManagement';
import BannerManagement from './BannerManagement';

const AdminPage = () => {
  const navigate = useNavigate();
  const [selectedShop, setSelectedShop] = useState('home');
  const [userRole, setUserRole] = useState('admin'); // 실제로는 로그인 시 설정되어야 함

  const shops = {
    home: {
      name: '피망 웹상점',
      roles: ['admin', 'home_manager'],
      path: '/admin/home'
    },
    newmajgo: {
      name: '뉴맞고 웹상점',
      roles: ['admin', 'newmajgo_manager'],
      path: '/admin/newmajgo'
    },
    poker: {
      name: '포커 웹상점',
      roles: ['admin', 'poker_manager'],
      path: '/admin/poker'
    }
  };

  const hasAccess = (shop) => {
    return shop.roles.includes(userRole);
  };

  const handleShopChange = (shopKey) => {
    setSelectedShop(shopKey);
    navigate(shops[shopKey].path);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <nav className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex">
              <div className="flex-shrink-0 flex items-center">
                <h1 className="text-xl font-bold">웹상점 관리자</h1>
              </div>
              <div className="hidden sm:ml-6 sm:flex sm:space-x-8">
                {Object.entries(shops).map(([key, shop]) => (
                  hasAccess(shop) && (
                    <button
                      key={key}
                      onClick={() => handleShopChange(key)}
                      className={`${
                        selectedShop === key
                          ? 'border-blue-500 text-gray-900'
                          : 'border-transparent text-gray-500 hover:border-gray-300 hover:text-gray-700'
                      } inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium`}
                    >
                      {shop.name}
                    </button>
                  )
                ))}
              </div>
            </div>
          </div>
        </div>
      </nav>

      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="border-4 border-dashed border-gray-200 rounded-lg p-4">
            <Routes>
              <Route path="/" element={
                <div className="text-center">
                  <h2 className="text-2xl font-bold mb-4">웹상점을 선택해주세요</h2>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    {Object.entries(shops).map(([key, shop]) => (
                      hasAccess(shop) && (
                        <button
                          key={key}
                          onClick={() => handleShopChange(key)}
                          className="p-4 border rounded-lg hover:bg-gray-50"
                        >
                          <h3 className="text-lg font-semibold">{shop.name}</h3>
                        </button>
                      )
                    ))}
                  </div>
                </div>
              } />
              <Route path="/home/*" element={
                hasAccess(shops.home) ? (
                  <div>
                    <h2 className="text-2xl font-bold mb-4">피망 웹상점 관리</h2>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                      <Link to="categories" className="p-4 border rounded-lg hover:bg-gray-50">
                        <h3 className="text-lg font-semibold">카테고리 관리</h3>
                      </Link>
                      <Link to="products" className="p-4 border rounded-lg hover:bg-gray-50">
