import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // useNavigate 추가

const AddShop = () => {
  const [shopName, setShopName] = useState('');
  const [shopDescription, setShopDescription] = useState('');
  const [shopImage, setShopImage] = useState('');
  const [shopCategory, setShopCategory] = useState('');
  const [shopList, setShopList] = useState([
    { name: '피망 웹상점(홈)', description: '피망 웹상점 설명', category: '카테고리' },
    { name: '뉴맞고 웹상점', description: '뉴맞고 웹상점 설명', category: '카테고리' },
    { name: '포커 웹상점', description: '포커 웹상점 설명', category: '카테고리' },
  ]); // 상점 리스트 상태 초기화
  const [isModalOpen, setIsModalOpen] = useState(false); // 모달 상태 추가
  const navigate = useNavigate(); // useNavigate 사용

  const handleAddShop = (e) => {
    e.preventDefault();
    const newShop = {
      name: shopName,
      description: shopDescription,
      category: shopCategory,
      category1: document.getElementById('category1').value, // 카테고리1
      category4: document.getElementById('category4').value, // 카테고리4
      siteType: document.getElementById('siteType').value, // 사이트 유형
    };
    console.log('새 상점 추가:', newShop);

    // 상점 리스트에 추가
    setShopList([
      ...shopList,
      newShop
    ]);

    // 추가 후 초기화
    setShopName('');
    setShopDescription('');
    setShopImage('');
    setShopCategory('');
    setIsModalOpen(false); // 모달 닫기
  };

  const handleDeleteShop = (index) => {
    const updatedShopList = shopList.filter((_, i) => i !== index);
    setShopList(updatedShopList); // 삭제 후 리스트 업데이트
    console.log('상점 삭제:', shopList[index].name);
  };

  return (  
        <div className="bg-gray-100">
          <div className="flex justify-between  w-full">    
          <div className="w-full p-6 bg-white rounded-lg shadow-md">
          <h3 className="text-2xl font-bold">상점 리스트</h3>
          <div className="flex justify-end mb-6">
        <button
          onClick={() => setIsModalOpen(true)}
          className="bg-blue-500 text-white px-4 py-2 rounded"        >
          상점 추가
        </button>


      </div>
        <ul className="mt-2">
          {shopList.map((shop, index) => (
            <li key={index} className="flex justify-between items-center border-b py-2">
              <div className="flex-grow">
                <strong>{shop.name}</strong> - {shop.category}
                <div className="text-sm text-gray-600">
                  카테고리 1: {shop.category1 || '없음'}, 카테고리 4: {shop.category4 || '없음'}, 사이트 유형: {shop.siteType || '없음'}
                </div>
              </div>
              <button
                onClick={() => handleDeleteShop(index)}
                className="text-red-500 hover:underline"
              >
                삭제
              </button>
            </li>
          ))}

        </ul>

      </div>


       </div>

      {/* 모달 */}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white rounded-lg p-6 w-1/3">
            <h2 className="text-2xl font-bold mb-4">상점 추가</h2>
            <form onSubmit={handleAddShop}>
              <div>
                <label className="block text-sm font-medium text-gray-700">상점 이름</label>
                <input
                  type="text"
                  value={shopName}
                  onChange={(e) => setShopName(e.target.value)}
                  className="border rounded px-3 py-2 w-full"
                  required
                />
              </div>
              <div className="mt-4">
                <label className="block text-sm font-medium text-gray-700">상점 설명</label>
                <textarea
                  value={shopDescription}
                  onChange={(e) => setShopDescription(e.target.value)}
                  className="border rounded px-3 py-2 w-full"
                  rows="4"
                  required
                />
              </div>

              {/* 카테고리 셀렉트 박스 추가 */}
              <div className="mt-4">
                <label className="block text-sm font-medium text-gray-700">카테고리 1</label>
                <select id="category1" className="border rounded px-3 py-2 w-full">
                  <option value="">선택하세요</option>
                  <option value="카테고리1-1">카테고리1-1</option>
                  <option value="카테고리1-2">카테고리1-2</option>
                </select>
              </div>
              <div className="mt-4">
                <label className="block text-sm font-medium text-gray-700">카테고리 2</label>
                <select className="border rounded px-3 py-2 w-full">
                  <option value="">선택하세요</option>
                  <option value="카테고리2-1">카테고리2-1</option>
                  <option value="카테고리2-2">카테고리2-2</option>
                </select>
              </div>
              <div className="mt-4">
                <label className="block text-sm font-medium text-gray-700">카테고리 3</label>
                <select className="border rounded px-3 py-2 w-full">
                  <option value="">선택하세요</option>
                  <option value="카테고리3-1">카테고리3-1</option>
                  <option value="카테고리3-2">카테고리3-2</option>
                </select>
              </div>
              <div className="mt-4">
                <label className="block text-sm font-medium text-gray-700">카테고리 4</label>
