import React, { useState } from 'react';

const BannerManagement = () => {
  const [banners, setBanners] = useState([
    {
      id: 1,
      title: "포커 칩 이벤트",
      image: "https://dl.poker.pmang.cloud/upload/pk_webmain_sales_2503_753x306.jpg",
      link: "https://www.pmang.com/poker",
      priority: 1,
      startDate: "2024-04-01",
      endDate: "2024-04-30",
      isActive: true
    },
    {
      id: 2,
      title: "뉴맞고 이벤트",
      image: "https://dl.gostop.pmang.cloud/upload/mg_4_PC_684x355.jpg",
      link: "https://www.pmang.com/newmajgo",
      priority: 2,
      startDate: "2024-04-15",
      endDate: "2024-05-15",
      isActive: true
    },
    {
      id: 3,
      title: "포커 칩 미션",
      image: "https://dl.poker.pmang.cloud/upload/230614_pokerchipmion_main(753x306).jpg",
      link: "https://www.pmang.com/poker/mission",
      priority: 3,
      startDate: "2024-04-10",
      endDate: "2024-04-20",
      isActive: false
    }
  ]);

  const [newBanner, setNewBanner] = useState({
    title: '',
    image: '',
    link: '',
    priority: '',
    startDate: '',
    endDate: '',
    isActive: true
  });

  const handleAddBanner = () => {
    if (newBanner.image) {
      const banner = {
        id: Date.now(),
        ...newBanner
      };
      setBanners([...banners, banner]);
      setNewBanner({
        title: '',
        image: '',
        link: '',
        priority: '',
        startDate: '',
        endDate: '',
        isActive: true
      });
    }
  };

  const handleDeleteBanner = (id) => {
    setBanners(banners.filter(banner => banner.id !== id));
  };

  const handleUpdateBanner = (id, field, value) => {
    setBanners(banners.map(banner => 
      banner.id === id ? { ...banner, [field]: value } : banner
    ));
  };

  return (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-2xl font-bold mb-6">배너 관리</h2>
      
      <div className="mb-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input
            type="text"
            value={newBanner.title}
            onChange={(e) => setNewBanner({...newBanner, title: e.target.value})}
            placeholder="배너 제목"
            className="border rounded px-3 py-2"
          />
          <input
            type="text"
            value={newBanner.image}
            onChange={(e) => setNewBanner({...newBanner, image: e.target.value})}
            placeholder="배너 이미지 URL"
            className="border rounded px-3 py-2"
          />
          <input
            type="text"
            value={newBanner.link}
            onChange={(e) => setNewBanner({...newBanner, link: e.target.value})}
            placeholder="링크 URL"
            className="border rounded px-3 py-2"
          />
          <input
            type="number"
            value={newBanner.priority}
            onChange={(e) => setNewBanner({...newBanner, priority: e.target.value})}
            placeholder="우선순위"
            className="border rounded px-3 py-2"
          />
          <input
            type="date"
            value={newBanner.startDate}
            onChange={(e) => setNewBanner({...newBanner, startDate: e.target.value})}
            placeholder="시작일"
            className="border rounded px-3 py-2"
          />
          <input
            type="date"
            value={newBanner.endDate}
            onChange={(e) => setNewBanner({...newBanner, endDate: e.target.value})}
            placeholder="종료일"
            className="border rounded px-3 py-2"
          />
        </div>
        <button
          onClick={handleAddBanner}
          className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          배너 추가
        </button>
      </div>

      <div className="space-y-4">
        {banners.map(banner => (
          <div key={banner.id} className="border rounded p-4">
            <div className="flex justify-between items-start">
              <div className="flex-1">
                <div className="flex items-center gap-4 mb-2">
                  <h3 className="font-bold">{banner.title}</h3>
                  <span className="text-sm text-gray-500">우선순위: {banner.priority}</span>
                  <select
                    value={banner.isActive}
                    onChange={(e) => handleUpdateBanner(banner.id, 'isActive', e.target.value === 'true')}
                    className="border rounded px-2 py-1 text-sm"
                  >
                    <option value="true">노출</option>
                    <option value="false">미노출</option>
                  </select>
                </div>
                <p className="text-sm text-blue-500 mb-2">{banner.link}</p>
                <div className="flex gap-4 text-sm text-gray-500 mb-2">
                  <span>시작일: {banner.startDate}</span>
                  <span>종료일: {banner.endDate}</span>
                </div>
                {banner.image && (
                  <img 
                    src={banner.image} 
                    alt={banner.title}
                    className="mt-2 max-w-xs rounded"
                  />
                )}
              </div>
              <div className="flex flex-col gap-2">
                <input
                  type="date"
                  value={banner.startDate}
                  onChange={(e) => handleUpdateBanner(banner.id, 'startDate', e.target.value)}
                  className="border rounded px-2 py-1 text-sm"
                />
                <input
                  type="date"
                  value={banner.endDate}
                  onChange={(e) => handleUpdateBanner(banner.id, 'endDate', e.target.value)}
                  className="border rounded px-2 py-1 text-sm"
                />
                <button
                  onClick={() => handleDeleteBanner(banner.id)}
                  className="text-red-500 hover:text-red-600 text-sm"
                >
                  삭제
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BannerManagement;
